export class MediaListComponent {
  constructor(media, router) {
    this.media = media;
    this.router = router;
    this.items = [];
  }
  ngOnInit() {
    this.media.list().subscribe(res => this.items = res);
  }
  getRating(m) {
    return m._rating || 0;
  }
  onRate(m, value) {
    this.media.patchRating(m._id || m.id, {
      rating: value
    }).subscribe(r => {
      m._rating = value;
    });
  }
  view(m) {
    this.router.navigate(['/media', m._id || m.id]);
  }
}
